class Solution:
    def minSubarray(self, nums: List[int], p: int) -> int:
        pre = list(accumulate(nums, add))
        mod = pre[-1] % p
        hashT = {0:-1}
        if mod == 0: return 0

        res = len(nums)
        for i in range(len(nums)):
            curmod = pre[i] % p
            tarmod = (curmod - mod + p) % p
            if tarmod in hashT:
                dis = i - hashT[tarmod]
                res = dis if dis < res else res
                if res == 1 and len(nums) != 1:
                    return 1
            hashT[curmod] = i
        if res == len(nums):
            res = -1
        return res


```

