class Solution {
    int sum = 0;
    List<Integer> list = new ArrayList<>();
    public int sumNumbers(TreeNode root) {
        dfs(root);
        int res = 0;
        for (int i:list) {
            res += i;
        }
        return res;
    }

    public void dfs(TreeNode root){
        if(root==null)
            return;
        sum = sum * 10 + root.val;

        if(root.left!=null){
            dfs(root.left);
        }
        if(root.right!=null){
            dfs(root.right);
        }

        if (root.left == null && root.right==null){
            // root是叶子节点了
            list.add(sum);
        }
        sum = (sum - root.val) / 10;
    }
}
