class Solution {
    public int[] twoSum(int[] nums, int target) {
        HashMap<Integer,Integer> map = new HashMap<>();
        int n = nums.length;
        for (int i=0 ; i < n; i++) {
            if (map.containsKey(target - nums[i])) {
                int[] res = new int[2];
                res[0] = map.get(target-nums[i]);
                res[1] = i;
                return res;
            }
            map.put(nums[i],i);
        }
        return new int[2];
