class Solution {
    public ListNode middleNode(ListNode head) {
        if (head.next == null)
            return head;

        ListNode slow = head;
        ListNode quick = head;

        while (quick != null && quick.next != null) {
            slow = slow.next;
            quick = quick.next.next;
        }

        return slow;
    }
}
