# 思路
在图中寻找入度为n-1，出度为0的顶点

# 代码
```
class Solution {
    public int findJudge(int n, int[][] trust) {
        int[][] graph = new int[n + 1][n + 1];
        for (int[] edge : trust) {
            int a = edge[0];
            int b = edge[1];
            graph[a][b] = 1;
        }

        for (int i = 1; i <= n; i++) {
            if (inDot(graph, i, n) == n - 1 && outDot(graph, i, n) == 0) {
                return i;
            }
        }
        return -1;
    }

    private int inDot(int[][] graph, int k, int n) {
        int res = 0;
        for (int i = 1; i <= n; i++) {
            if (graph[i][k] == 1) {
                res++;
            }
        }
        return res;
    }

    private int outDot(int[][] graph, int k, int n) {
        int res = 0;
        for (int i = 1; i <= n; i++) {
            if (graph[k][i] == 1) {
                res++;
            }
        }
        return res;
    }
}
