class Solution {
    int[][] direction = {{-1, 0}, {0, 1}, {1, 0}, {0, -1}};

    // 对应 上 右 下 左   u r  d l
    public boolean judgeCircle(String moves) {
        int[] loc = new int[]{0, 0};
        for (int i = 0; i < moves.length(); i++) {
            char c = moves.charAt(i);
            switch (c) {
                case 'U': {
                    loc[0] += direction[0][0];
                    loc[1] += direction[0][1];
                    break;
                }
                case 'R': {
                    loc[0] += direction[1][0];
                    loc[1] += direction[1][1];
                    break;
                }
                case 'D': {
                    loc[0] += direction[2][0];
                    loc[1] += direction[2][1];
                    break;
                }
                case 'L': {
                    loc[0] += direction[3][0];
                    loc[1] += direction[3][1];
                    break;
                }
            }
        }
        return loc[0] == 0 && loc[1] == 0;
    }
}
