class Solution {
   public int[] sortArray(int[] nums) {
        int[] tmp = new int[nums.length];
        sort(nums, 0, nums.length - 1, tmp);
        return nums;
    }

    private void sort(int[] nums, int a, int b, int[] tmp) {
        if (a == b)
            return;

        int mid = a + (b - a) / 2;
        sort(nums, a, mid, tmp);
        sort(nums, mid + 1, b, tmp);

        int l = a;
        int r = mid + 1;
        int index = a;
        while (l <= mid && r <= b) {
            if (nums[l] < nums[r]) {
                tmp[index++] = nums[l++];
            } else {
                tmp[index++] = nums[r++];
            }
        }
        while (l <= mid) {
            tmp[index++] = nums[l++];
        }
        while (r <= b) {
            tmp[index++] = nums[r++];
        }
        for (int i = a; i <= b; i++) {
            nums[i] = tmp[i];
        }
    }
}
