class Solution {
    public int mySqrt(int x) {
        // 解空间在[0,x]
        // 找最后一个比它小的，也就是第一个比他大的,也就是最左边比它大的
        int l = 0;
        int r = Math.min(x,46340);
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (mid * mid >= x) {
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        if (l*l == x)
            return l;
        return l - 1;
    }
}
