func findBottomLeftValue(root *TreeNode) int {
    queue := []*TreeNode{root}
    leftNum := root.Val
    for len(queue) != 0 {
        n := len(queue)
        for i:=0;i<n;i++ {
            if i == 0{
                node := queue[0]
                queue = queue[1:]
                leftNum = node.Val
                if node.Left != nil {
                    queue = append(queue, node.Left)
                }
                if node.Right != nil {
                    queue = append(queue, node.Right)
                }
            } else {
