## 思路
深度优先遍历生成前序序列。
## 代码
```python
class Codec:

    def serialize(self, root):
        """Encodes a tree to a single string.

        :type root: TreeNode
        :rtype: str
        """
        if not root:
            return '#'
        left_tree = self.serialize(root.left)
        right_tree = self.serialize(root.right)
        return str(root.val) + ',' + left_tree + ',' + right_tree


    def deserialize(self, data):
        """Decodes your encoded data to tree.

        :type data: str
        :rtype: TreeNode
        """
        str_list = data.split(',')
        tree_node = self.deser_dfs(str_list)
        return tree_node

    def deser_dfs(self, lists):
        node = lists.pop(0)
        if node == '#':
            return None
        tree_node = TreeNode(int(node))
        tree_node.left = self.deser_dfs(lists)
        tree_node.right = self.deser_dfs(lists)
        return tree_node
```
```go
import (
    "strconv"
    "strings"
)

type Codec struct {

}

func Constructor() Codec {
    return Codec{}
}

// Serializes a tree to a single string.
func (this *Codec) serialize(root *TreeNode) string {
    if root == nil {
        return "#"
    }
    leftTree := this.serialize(root.Left)
    rightTree := this.serialize(root.Right)
    return strconv.Itoa(root.Val) + "," + leftTree + "," + rightTree
}

// Deserializes your encoded data to tree.
func (this *Codec) deserialize(data string) *TreeNode {    
    lists := strings.Split(data, ",")
    treeNode := deserDFS(&lists)
    return treeNode
}

func deserDFS(lists *[]string) *TreeNode {
    node := (*lists)[0]
    *lists = (*lists)[1:]
    if node == "#" {
        return nil
    }
    val, _ := strconv.Atoi(node)
    treeNode := &TreeNode{val, nil, nil}
    treeNode.Left = deserDFS(lists)
    treeNode.Right = deserDFS(lists)
    return treeNode
}
```
