class Solution:
    def removeDuplicates(self, nums: List[int]) -> int:
        length = len(nums)
        if length == 0 or length == 1:
            return length
        cur = 1
        diff = 0
        while cur < length:
            if nums[cur] == nums[diff]:
                cur += 1
            else:
                nums[diff+1]=nums[cur]
                diff += 1
                cur += 1
        return diff + 1
```
