func removeDuplicates(nums []int) int {
    if len(nums) == 0 || len(nums) == 1 {
        return len(nums)
    }
    diff := 0
    cur := 1
    length := len(nums)
    for cur < length {
        if nums[cur] == nums[diff] {
            cur++
        } else {
            nums[diff+1] = nums[cur]
            diff++
            cur++
        }
    }
    return diff+1
}
