func findJudge(n int, trust [][]int) int {
    degree := map[int]int{}
    if len(trust) == 0 {
        degree[1] = 0
    }
    for _, edge := range trust {
        degree[edge[0]]--
        degree[edge[1]]++
    }
    for key, value := range degree {
        if value == n - 1 {
            return key
        }
    }
    return -1
}
