class Solution:
    def numberOfRounds(self, loginTime: str, logoutTime: str) -> int:
        start_minute = 60*int(loginTime[:2])+int(loginTime[3:])
        end_minute = 60*int(logoutTime[:2])+int(logoutTime[3:])
        if start_minute > end_minute:
            end_minute += 1440
        # 开始时间向上取整
        start_minute = (start_minute+14) // 15 *15
        # 结束时间向下取整
        end_minute = end_minute // 15 * 15
        return max(0, (end_minute-start_minute)//15)
