```go
func numberOfRounds(loginTime string, logoutTime string) int {
    startHourInt, _ := strconv.Atoi(strings.Split(loginTime,":")[0])
    startMinuteInt, _ := strconv.Atoi(strings.Split(loginTime,":")[1])
    startMinute := 60*startHourInt + startMinuteInt
    endHourInt, _ := strconv.Atoi(strings.Split(logoutTime,":")[0])
    endMinuteInt, _ := strconv.Atoi(strings.Split(logoutTime,":")[1])
    endMinute := 60*endHourInt + endMinuteInt
    if startMinute > endMinute {
        endMinute += 1440
    }
    // 开始时间向上取整
    startMinute = (startMinute+14) / 15 * 15
    // 结束时间向下取整
    endMinute = endMinute / 15 * 15
    if (endMinute - startMinute) / 15 < 0 {
        return 0
    }
    return (endMinute - startMinute) / 15
}
