    # Definition for a binary tree node.
    # class TreeNode:
    #     def __init__(self, val=0, left=None, right=None):
    #         self.val = val
    #         self.left = left
    #         self.right = right
    class Solution:
        def sumNumbers(self, root: Optional[TreeNode]) -> int:
            if not root:
                return 0
            return self.helper(root, 0)


        def helper(self, root, sumNum):
            if not root.left and not root.right:
                return sumNum * 10 + root.val
            if not root.left:
                return self.helper(root.right, sumNum * 10 + root.val)
            if not root.right:
                return self.helper(root.left, sumNum * 10 + root.val)
            left = self.helper(root.left, sumNum * 10 + root.val)
            right = self.helper(root.right, sumNum * 10 + root.val)
            return left + right
