class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        //改进，都走相同的长度a+b
        if(headA==nullptr || headB==nullptr){
            return nullptr;
        }
        ListNode* tmpA=headA,*tmpB=headB;
        while(tmpA!=tmpB){
            tmpA=tmpA==nullptr?headB:tmpA->next;
            tmpB=tmpB==nullptr?headA:tmpB->next;
