class Solution {
public:
    ListNode *detectCycle(ListNode *head) {
        //2、双指针，快慢指针
        ListNode* fast=head,*slow=head;
        while(slow!=nullptr && fast!=nullptr && fast->next!=nullptr){
            fast=fast->next->next;
            slow=slow->next;
            if(fast==slow){
                ListNode* tmp1=head;
                ListNode* tmp2=fast;
                while(tmp1!=tmp2){
                    tmp1=tmp1->next;
                    tmp2=tmp2->next;
                }
                return tmp2;
            }
        }
        return nullptr;
    }
};
