class Solution {
public:
    //广度优先搜索，队列
    int findBottomLeftValue(TreeNode* root) {
        queue<TreeNode*> Q;
        int left;
        if(root==nullptr){
            return 0;
        }
        Q.push(root);
        while(!Q.empty()){
            int size=Q.size();
            left=Q.front()->val;//只保留每一层节点的头结点
            while(size--){//每次处理一层节点
                TreeNode* tmp=Q.front();
                Q.pop();
                if(tmp->left!=nullptr){
                    Q.push(tmp->left);
                }
                if(tmp->right!=nullptr){
                    Q.push(tmp->right);
                }
            } 
        }
        return left;
    }
};
```
