class Codec {
public:
    void rserialize(TreeNode* root,string& ret){
        if(root==nullptr){
            ret+="None,";
        }
        else{
            //要以“，”作为分隔符，先序遍历，中左右
            ret += to_string(root->val) + ",";
            rserialize(root->left,ret);
            rserialize(root->right,ret);
        }

    }
    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        //序列化，将树变为字符串
        string ret;
        rserialize(root,ret);
        return ret;
    }
    //此处传入引用
    TreeNode* rdeserialize(list<string>& Lnode){
        if(Lnode.front()=="None"){
            Lnode.erase(Lnode.begin());
            return nullptr;
        }
        TreeNode* root=new TreeNode(stoi(Lnode.front()));
        Lnode.erase(Lnode.begin());
        root->left=rdeserialize(Lnode);
        root->right=rdeserialize(Lnode);
        return root;
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        //反序列化，将字符串，变成树
        //将字符串进行分割
        list<string> Lnode;
        string str;
        //要对其进行修改，因此用引用
