        for(vector<int>num_0 : points){
            unordered_map<int,int> myMap;
            for(auto num_1 : points){
                int distance=(num_0[0]-num_1[0])*(num_0[0]-num_1[0])+(num_0[1]-num_1[1])*(num_0[1]-num_1[1]);
