class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        //双指针+哈希表
        int Max=0;
        unordered_map<char,int> maMap;
        int left=0,right=0;
        while(right<s.size()){
            unordered_map<char,int>::iterator it=maMap.find(s[right]);
            if(it!=maMap.end()){
                if((it->second)>=left){
                    left=(it->second)+1;
                }  
            }
            maMap[s[right]]=right;
            right++;
            Max=max(Max,right-left);
        }
        return Max;
    }
};
