class Solution {
public:
    int minCharacters(string a, string b) {
        int res=INT_MAX;
        //小写字母，用数组表示哈希表统计，足够
        vector<int>countA(26,0);
        vector<int>countB(26,0);
        int sizeA=a.size();
        int sizeB=b.size();
        for(char ch:a){
            countA[ch-'a']++;
        }
        for(char ch:b){
            countB[ch-'a']++;
        }
        //利用前缀和，后缀和
        int sumA=0,sumB=0;
        for(int i=0;i<25;i++){
            sumA+=countA[i];
            sumB+=countB[i];
            res=min(min(res,sizeA-countA[i]+sizeB-countB[i]),min(sizeA-sumA+sumB,sizeB-sumB+sumA));
        }
        res=min(res,sizeA-countA[25]+sizeB-countB[25]);
        return res;
    }
};
```
