    int mySqrt(int x) {
        //二分法.left从1开始，避免mid=0,出现除以0的报错
        int left=1,right=x;
        int mid;
        while(left<=right){
            mid=left+(right-left)/2;
            if((x/mid)>mid){
                left=mid+1;
            }
            else if((x/mid)<mid){
                right=mid-1;
            }
            else{
                return mid;
            }
