var shortestToChar = function(s, c) {
    let len = s.length
    let left = []
    let right = []
    for (let i = 0;i <len;i++) {
        if (s.indexOf(c,i)==-1) {
            left.push(Infinity)
        } else {
            left.push(s.indexOf(c,i)-i)
        }  
    }
    for (let i = len-1;i >= 0;i--) {
        if (s.lastIndexOf(c,i)==-1) {
            right.unshift(Infinity)
        } else {
            right.unshift(i-s.lastIndexOf(c,i))
