class Solution {
    public boolean isSameTree(TreeNode p, TreeNode q) {
        if(p == null && q == null) {
            return true;
        }
        if(p == null || q == null) {
            return false;
        }
        Queue<TreeNode> q1 = new LinkedList<>();
        Queue<TreeNode> q2 = new LinkedList<>();
        q1.offer(p);
        q2.offer(q);

        while(!q1.isEmpty() && !q2.isEmpty()) {
            TreeNode n1 = q1.poll();
            TreeNode n2 = q2.poll();
            if(n1.val != n2.val) {
                return false;
            }
            TreeNode left1 = n1.left, right1 = n1.right, left2 = n2.left, right2 = n2.right;
            if(left1 != null && left2 != null) {
                q1.offer(left1);
                q2.offer(left2);
            } else if(left1 == null ^ left2 == null) {
                return false;
            }
            if(right1 != null && right2 != null) {
                q1.offer(right1);
                q2.offer(right2);
            } else if(right1 == null ^ right2 == null) {
                return false;
            }
        }
        return q1.isEmpty() && q2.isEmpty();
    }
}
]
