class Solution {
    int bottom = 0;
    int val;

    public int findBottomLeftValue(TreeNode root) {
        dfs(root, 1);
        return val;
    }

    private void dfs(TreeNode root, int level) {
            if(root != null) {
                if(root.left == null && root.right == null) {
                    if(level > bottom) {
                        val = root.val;
                        bottom = level;
                    }
                }
                dfs(root.left, level + 1);
                dfs(root.right, level + 1);
            }
        }
}
