        Queue<TreeNode> q = new LinkedList<>();
        q.offer(root);
        TreeNode node = new TreeNode();
        while(!q.isEmpty()) {
            node = q.poll();
            if(node.right != null) {
                q.offer(node.right);
            }
            if(node.left != null) {
                q.offer(node.left);
            }
