        int l = 0, r = x;
        while(l <= r) {
            int mid = l + (r - l) / 2;
            long cur = (long)mid * mid;
            if(cur == x) {
                return mid;
            } else if (cur < x) {
                l = mid + 1;
            } else {
                r = mid - 1;
            }
        }

        return --l;
    }
}
