class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {
        if(!head) return head;
        ListNode* tail = new ListNode();
        int n = 0;
        for(auto p = head; p; p = p->next){
            tail = p;
            n ++;
        }
        k %= n;//如果k过大，对n取余
        auto p = head;
        for(int i = 0; i < n - k - 1; i ++) p = p->next;
        tail->next = head;
        head = p->next;
        p->next = nullptr;
        return head;
    }
};
