int* shortestToChar(char * s, char c, int* returnSize) {
    int len = strlen(s);
    int* res = malloc(sizeof(int) * len);

    res[0] = s[0] == c ? 0 : len;

    for (int i = 1; i < len; ++i)
        res[i] = s[i] == c ? 0 : res[i - 1] + 1;

    for (int i = len - 2; i >= 0; --i)
        res[i] = fmin(res[i], res[i + 1] + 1);

    *returnSize = len;
    return res;
}
