class Solution {
public:
    bool isNumber(const string& str) {
        for (char const &c : str) {
            if (std::isdigit(c) == 0) return false;
        }
        return true;
    }

    string repeat(const string& s, int t) {
        string res;
        while (t-- > 0) res += s;
        return res;
    }

    string decodeString(string s) {
        string res;
        stack<string> stk;

        for (char c : s) {
            if (c != ']') {
                stk.push(string(1, c));
                continue;
            }

            string p = "";
            while (!stk.empty() && stk.top() != "[") {
                p = stk.top() + p; stk.pop();
            }
            stk.pop(); // pop out '['

            string t = "";
            while (!stk.empty() && isNumber(stk.top())) {
                t = stk.top() + t; stk.pop();
            }
            stk.push(repeat(p, stoi(t)));
        }

        while (!stk.empty()) {
            res = stk.top() + res; stk.pop();
        }
        return res;
    }
};
