class Solution {
public:
    int maxChunksToSorted(vector<int>& arr) {
        stack<int> stk;
        for (auto n : arr) {
            if (stk.empty() || n >= stk.top())
                stk.push(n); // new chunk
            else {
                int temp = stk.top(); stk.pop();
                // combine chunks
                while (!stk.empty() && stk.top() > n)
                    stk.pop();
                stk.push(temp);
            }
        }
        return stk.size();
    }
};
