class Solution {
    public ListNode swapPairs(ListNode head) {
        if (head == null || head.next == null) {
            return head;
        }
        ListNode newHead = head.next;
        ListNode pre = newHead;
        ListNode cur = head;
        while (cur != null && cur.next != null) {
            ListNode toSwap = cur.next;
            cur.next = toSwap.next;
            pre.next = toSwap;
            toSwap.next = cur;
            pre = cur;
            cur = cur.next;
        }
        return newHead;
    }
}
