public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        int sizeA = 0;
        int sizeB = 0;
        ListNode pA = headA;
        ListNode pB = headB;
        while (pA != null) {
            pA = pA.next;
            sizeA++;
        }
        while (pB != null) {
            pB = pB.next;
            sizeB++;
        }
        pA = headA;
        pB = headB;
        for (int i = 0; i < Math.abs(sizeA - sizeB); i++) {
            if (sizeA > sizeB) {
                pA = pA.next;
            } else {
                pB = pB.next;
            }
        }
        while (pA != null && pA != pB) {
            pA = pA.next;
            pB = pB.next;
        }
        return pA;
    }
}
