class LRUCache {

    Node head;
    Node tail;
    Map<Integer, Node> map;
    int size;
    int capacity;

    public LRUCache(int capacity) {
        this.size = 0;
        this.capacity = capacity;
        map = new HashMap<>();
        head = new Node(0, 0);
        tail = new Node(0, 0);
        head.next = tail;
        tail.pre = head;
    }

    public int get(int key) {
        if (map.containsKey(key)) {
            Node curNode = map.get(key);
            moveToTail(curNode);
            return curNode.val;
        }
        return -1;
    }

    public void put(int key, int value) {
        if (!map.containsKey(key)) {
            size++;
        }
        Node curNode = map.getOrDefault(key, new Node(key, value));
        curNode.val = value;
        map.put(key, curNode);
        moveToTail(curNode);

        if (size > capacity) {
            Node evict = head.next;
            head.next = evict.next;
            evict.next.pre = head;
            map.remove(evict.key);
            size--;
        }  
        // for (Map.Entry<Integer, Node> e : map.entrySet()) {
        //     System.out.print(e.getKey()+" "+e.getValue().val+"; ");
        // }
        // for (Node p = head; p != null; p = p.next) {
        //     System.out.print(p.key+"|"+p.val+" ");
        // }
        // System.out.println();
    }

    private void moveToTail(Node curNode) {
        if (curNode.next != null) {
            curNode.pre.next = curNode.next;
            curNode.next.pre = curNode.pre;
        }
        curNode.next = tail;
        curNode.pre = tail.pre;
        tail.pre.next = curNode;
        tail.pre = curNode;
    }

    class Node {
        Node next;
        Node pre;
        int key;
        int val;
        public Node(int key, int val) {
            this.key = key;
            this.val = val;
        }
        public String toString() {
            return key+ " "+val;
        }
    }
}
```
