        q.add(root);
        while (!q.isEmpty()) {
            depth++;
            int curLevel = q.size();
            for (int i = 0; i < curLevel; i++) {
                TreeNode cur = q.poll();
                if (cur.left != null) {
                    q.add(cur.left);
