class Solution {
    public int numberOfRounds(String loginTime, String logoutTime) {
        String[] login = loginTime.split(":");
        int loginHour = Integer.valueOf(login[0]);
        int loginMinute = Integer.valueOf(login[1]);
        String[] logout = logoutTime.split(":");
        int logoutHour = Integer.valueOf(logout[0]);
        int logoutMinute = Integer.valueOf(logout[1]);

        if (loginHour > logoutHour || loginHour == logoutHour && loginMinute > logoutMinute) {
            logoutHour += 24;
        }

        int rounds = Math.max(0, (logoutHour - loginHour - 1) * 4);
        int loginHourMissedRounds = (loginMinute / 15) + (loginMinute % 15 != 0 ? 1 : 0);
        int logoutHourRounds = logoutMinute / 15;

        if (loginHour == logoutHour) {
            rounds += Math.max(0, logoutHourRounds - loginHourMissedRounds);
        } else {
            rounds += (4 - loginHourMissedRounds) + logoutHourRounds;
        }

        return rounds;
    }
}

