public String decodeString(String s) {
        Deque<Integer> stackNumber = new LinkedList<>();
        Deque<String> stackString = new LinkedList<>();
        StringBuilder stringBuilder = new StringBuilder();
        int number = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '[') {
                stackNumber.push(number);
                number = 0;
                stackString.push(stringBuilder.toString());
                stringBuilder = new StringBuilder();
            } else if (s.charAt(i) == ']') {
                StringBuilder temp = new StringBuilder();
                int curr_number = stackNumber.pop();
                for (int j = 0; j < curr_number; j++) {
                    temp.append(stringBuilder);
                }
                stringBuilder = temp.insert(0, stackString.pop());
            } else if (s.charAt(i) > 47 && s.charAt(i) < 58) {
                number = 10 * number + (s.charAt(i) - '0');
            } else {
                stringBuilder.append(s.charAt(i));
            }
        }
        return stringBuilder.toString();
    }
