public int maxChunksToSorted(int[] arr) {
    Deque<Integer> stack = new LinkedList<>();
    stack.push(arr[0]);
    int head;
    for (int i = 1; i < arr.length; i++) {
    if (arr[i] >= stack.peek()) {
        stack.push(arr[i]);
    } else {
    head = stack.pop();
    while (!stack.isEmpty() && stack.peek() > arr[i]) {
        stack.pop();
    }
    stack.push(head);
    }
}
return stack.size();
