    public ListNode swapPairs(ListNode head) {
        if (head == null || head.next == null) {
            return head;
        }
        ListNode ans = new ListNode(0, head);
        ListNode temp = ans;
        ListNode next;
        ListNode node2;
        while (temp.next != null && temp.next.next != null) {
            //保留1
            next = temp.next;
            //保留2
            node2 = next.next;
            //pre-->2
            temp.next = node2;
            //1-->3
            next.next = node2.next;
            //2-->1
            node2.next = next;
            temp = next;
        }
        return ans.next;
    }
