    class LRUCache {
        class LinkedNode {
        int key;
        int value;
        LinkedNode pre;
        LinkedNode next;

            public LinkedNode() {

            }

            public LinkedNode(int _key, int _value) {
                key = _key;
                value = _value;
            }
        }

        private int size;
        int current;
        private Map<Integer, LinkedNode> keyValue;
        private LinkedNode head, tail;

        public LRUCache(int capacity) {
            size = capacity;
            current = 0;
            keyValue = new HashMap<>();
            head = new LinkedNode();
            tail = new LinkedNode();
            head.next = tail;
            tail.pre = head;
        }

        public int get(int key) {
            final LinkedNode orDefault = keyValue.getOrDefault(key, null);
            if (orDefault == null) {
                return -1;
            }
            removeNode(orDefault);
            addToHead(orDefault);
            return orDefault.value;
        }

        public void put(int key, int value) {
            LinkedNode valueNode = keyValue.get(key);
            if (keyValue.containsKey(key)) {
                valueNode.value = value;
                removeNode(valueNode);
                addToHead(valueNode);
                return;
            } else {
                valueNode = new LinkedNode(key, value);
                addToHead(valueNode);
                current++;
                if (current > size) {
                    int removeKey = tail.pre.key;
                    tail = tail.pre;
                    keyValue.remove(removeKey);
                    current--;
                }
            }
            keyValue.put(key, valueNode);
        }

        void removeNode(LinkedNode node) {
            node.pre.next = node.next;
            node.next.pre = node.pre;
        }

        void addToHead(LinkedNode node) {
            node.next = head.next;
            head.next.pre = node;
            head.next = node;
            node.pre = head;
        }
    }
```

