public boolean judgeCircle(String moves) {
    char[] chars = moves.toCharArray();
    int x = 0, y = 0;
    for (char c : chars) {
        if (c == 'U') {
            y--;
        } else if (c == 'D') {
            y++;
        } else if (c == 'L') {
            x--;
        } else if (c == 'R') {
            x++;
        }
    }
    return x == 0 && y == 0;
}
