def sortArray(self, nums):
    if len(nums)<=1:
        return nums
    mid=len(nums)//2
    left=self.sortArray(nums[:mid])
    right=self.sortArray(nums[mid:])
    return self.merge(left,right)
def merge(self,left,right):
    i,j=0,0
    res=[]
    while i<len(left) and j<len(right):
        if left[i]<=right[j]:
            res.append(left[i])
            i+=1
        else:
            res.append(right[j])
            j+=1
    res+=left[i:]
    res+=right[j:]
    return res

def mySqrt(self, x: int) -> int:
    ans, l, r = 0, 0, x
    while l <= r:
        mid = (l + r) // 2
        if mid ** 2 > x:
            r = mid - 1
        if mid ** 2 <= x:
            ans = mid
            l = mid + 1
    return int(ans)

def firstBadVersion(self, n):

    l,r=1,n
    while l<=r:
        mid=(l+r)//2
        if not isBadVersion(mid):
            l=mid+1
        else:
            r=mid-1
            ans=mid
    return ans

