class MyQueue {
    Deque<Integer> stackIn;
    Deque<Integer> stackOut;

    public MyQueue() {
        stackIn = new ArrayDeque<>();
        stackOut = new ArrayDeque<>();
    }

    //push时检查out栈里是否为空，否则倒到in栈里再进行push
    public void push(int x) {
        while(stackOut.size()>=1){
            stackIn.addFirst(stackOut.removeFirst());
        }
        stackIn.addFirst(x);
    }

    //pop同理
    public int pop() {
        while(stackIn.size()>=1){
            stackOut.addFirst(stackIn.removeFirst());
        }
        int res = stackOut.removeFirst();
        return res;
    }

    public int peek() {
        while(stackIn.size()>=1){
            stackOut.addFirst(stackIn.removeFirst());
        }
        int res = stackOut.getFirst();
        return res;
    }

    public boolean empty() {
        return stackIn.size()==0 && stackOut.size()==0;
    }
}
