class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        //遍历链表得出节点数listNum
        //首尾相接，在第listNum - k%listNum 处断开
        if(head == null) return null;
        int listNum = 1;
        ListNode temp = head;
        while(temp.next != null){
            temp = temp.next;
            listNum++;
        }
        //System.out.println("listNum: " + listNum);

        temp.next = head;

        int newHeadIndex = listNum - k%listNum;
        //System.out.println("newHeadIndex: " + newHeadIndex);
        temp = head;
        for(int i=1; i<newHeadIndex; i++){
            temp = temp.next;
        }
        head = temp.next;
        temp.next = null;
        //System.out.println("newHeadValue: " + head.val);
        return head;
    }
}
