class MyQueue {
    Stack<Integer> input;
    Stack<Integer> output;

    public MyQueue() {
        input = new Stack<>();
        output = new Stack<>();

    }

    public void push(int x) {
        input.push(x);

    }

    public int pop() {
        if(output.isEmpty()){   //must check if it's empty   
            while(!input.isEmpty()){
                output.push(input.pop());
            }     
        }
          return output.pop();

    }

    public int peek() {
        if(output.isEmpty()){//must check empty
            while(!input.isEmpty()){
                output.push(input.pop());
            }
        }
        return output.peek();
    }

    public boolean empty() {
        return output.isEmpty() && input.isEmpty();
    }
}


