var swapPairs = function(head) {
    if (!head || !head.next) return head
    let node = new ListNode('head')
    node.next = head
    let prev = node
    let curr = node.next
    let next = node.next.next
    while(next) {
        let nextNode = next.next
        curr.next = nextNode
        next.next = curr
        prev.next = next
        if(!nextNode) break
        prev = curr
        curr = nextNode
        next = nextNode.next
    }
    return node.next
};
