var detectCycle = function(head) {
    if(!head) return null
    let prev = head
    let curr = head
    while(curr) {
        prev = prev.next
        if(curr.next!=null){
            curr = curr.next.next
        }else{
            return null
        }
        if(prev==curr){
            let ptr = head;
            while (ptr !== prev) {
                ptr = ptr.next;
                prev = prev.next;
            }
            return ptr;
        }
    }
    return null;
