/**
 * @param {TreeNode} p
 * @param {TreeNode} q
 * @return {boolean}
 */
var isSameTree = function(p, q) {
    let res = true;
    let inorder = (p,q)=>{
        if(!res) return
        if(!p && q || p&&!q) {
            res=false;
            return;
        }
        else if(!p && !q){
            return;
        }else{
            if(p.val!=q.val){
                res=false;
                return;
            }
            inorder(p.left,q.left);
            inorder(p.right,q.right);
        }
    }
    inorder(p,q)
    return res
};

